#define ORC_AVX512_INSN_VALIDATE_OPERAND_12(op, n, reg)                        \
  if ((op->avx512_operands & ORC_AVX512_INSN_OPERAND_OP##n##_ZMM) &&           \
      orc_avx512_insn_validate_reg (reg)) {                                    \
    return TRUE;                                                               \
  } else if ((op->avx512_operands & ORC_AVX512_INSN_OPERAND_OP##n##_K) &&      \
      orc_avx512_insn_validate_mask_reg (reg)) {                               \
    return TRUE;                                                               \
  } else if ((op->avx512_operands & ORC_AVX_INSN_OPERAND_OP##n##_YMM) &&       \
      orc_avx512_insn_validate_avx_reg (reg)) {                                \
    return TRUE;                                                               \
  } else if ((op->avx512_operands & ORC_SSE_INSN_OPERAND_OP##n##_XMM) &&       \
      orc_avx512_insn_validate_sse_reg (reg)) {                                \
    return TRUE;                                                               \
  } else {                                                                     \
    ORC_ERROR ("Unsupported operand %d with reg %d for op %s", n, reg,         \
        op->name);                                                             \
    return FALSE;                                                              \
  }

typedef struct _OrcAVX512InsnOp {
  char name[16];
  unsigned int target_flags;
  unsigned int operands;
  unsigned int avx512_operands;
  OrcX86InsnOpcodePrefix prefix;
  OrcX86InsnOpcodeEscapeSequence escape;
  orc_uint32 opcode;
  orc_uint8 extension;
  orc_uint32 flags;
  OrcX86InsnTupleType tuple_type;
} OrcAVX512InsnOp;

